import React, {useEffect, useState} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import axios from "axios";

//function with return statement : => indicate that there is a return in ()
const ButtonPrimary = ({onClick, children}) => (
    <button className="btn btn-primary" onClick={onClick}>{children}</button>
);
const ButtonAge = ({onClick}) => (
    <button className="btn btn-primary" onClick={onClick}>Trier par age</button>
);

const SearchImput = ({props}) => (
    <div className="input-group mb-6">
        <input type="text" className="form-control" placeholder="Search..." aria-label="Search..." aria-describedby="basic-addon2" onChange={(e) => props.onChange(e)}></input>
        <div className="input-group-append">
            <span className="input-group-text" id="basic-addon2">üîç</span>
        </div>
    </div>
)
const Header = ({onFetchClick, sortUsersOnClick}) => (
    <div>
        <h1>React app 2</h1>
        <ButtonPrimary onClick={onFetchClick}>Fetch users</ButtonPrimary>
        <ButtonAge onClick={sortUsersOnClick}>Trier les utilisateurs </ButtonAge>
        <hr/>
    </div>
);

const Table = ({users}) =>(
    <table  className="table table-hover">
        <thead>
        <tr>
            <th>Photo</th>
            <th>Pr√©nom - Nom</th>
            <th>Email</th>
            <th>Tel</th>
            <th>Age</th>
        </tr>
        </thead>
        <tbody>
        {users.map(user => <Row user={user} key={user.id}/>)}
        </tbody>
    </table>
);

const Row = ({user}) => (
    <tr>
        <td><img src={user.picture} alt=""/></td>
        <td>{user.firstName} {user.lastName}</td>
        <td>{user.email}</td>
        <td>{user.phone}</td>
        <td>{user.age}</td>
    </tr>
);

const fetchUsers = async (userCount) => {
    try {
        const {data: {results}} = await axios.get('https://randomuser.me/api/?results=' + userCount)

        return results.map(result => ({
            id: result.login.uuid,
            firstName: result.name.first,
            lastName: result.name.last,
            phone: result.phone,
            email: result.email,
            age: result.dob.age,
            picture: result.picture.thumbnail
        }))
    } catch (e) {
        console.error(e);
        return []
    }

};

export const App = () => {

    const [users, setUsers] = useState([]);
    const [search, setSearch] = useState('');
    const [num, setNum] = useState(0);

    const handleFetchClick = () => {
        fetchUsers(10).then((newUsers) => setUsers([...users,...newUsers]));
    };

    const sortUsersOnClick = () => {
        setUsers(users => [...users].sort(function(a, b) {
                const textA = a.email.toUpperCase();
                const textB = b.email.toUpperCase();
                return (textA < textB) ? -1 : (textA > textB) ? 1 : 0;
            })
        )
    }
    const onChangeSearch = (e) => {
        let keyword = e.target.value
        setSearch(keyword)
    }

    useEffect(async () => {
            fetchUsers(5).then(users => setUsers(users));
        },
        [num]);


    return(
        <div className="container-fluid">
            <div id="app">
                <Header onFetchClick={handleFetchClick} />
                <Table users={users}/>
                <ButtonAge buttonName="Sort" onClick={sortUsersOnClick}/>
                <SearchImput onChange={onChangeSearch}/>
            </div>
            <Table users={resultat.filter((data)=>{
                if(data.name.first.toLowerCase().includes(search.toLowerCase()) || data.name.last.toLowerCase().includes(search.toLowerCase())){
                    return data
                }
            })}/>
        </div>
    )
};
