import React, {useEffect, useState} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'
import axios from "axios";

//function with return statement : => indicate that there is a return in ()
const ButtonPrimary = ({onClick, children}) => (
    <button className="btn btn-primary" onClick={onClick}>{children}</button>
);
const ButtonSort = ({onClick}) => (
    <button className="btn btn-primary" onClick={onClick}>Trier par âge</button>
)

const Header = ({onFetchClick, onTriClick, setSearch}) => (
    <div>
        <h1>React app 2</h1>
        <ButtonPrimary onClick={onFetchClick}>Fetch users</ButtonPrimary>
        <hr/>
    </div>
);

const Table = ({users}) =>(
    <table  className="table table-hover">
        <thead>
        <tr>
            <th>Photo</th>
            <th>Prénom - Nom</th>
            <th>Email</th>
            <th>Tel</th>
            <th>Age</th>
        </tr>
        </thead>
        <tbody>
        {users.map(user => <Row user={user} key={user.id}/>)}
        </tbody>
    </table>
);

const Row = ({user}) => (
    <tr>
        <td><img src={user.picture} alt=""/></td>
        <td>{user.firstName} {user.lastName}</td>
        <td>{user.email}</td>
        <td>{user.phone}</td>
        <td>{user.age}</td>
    </tr>
);

const fetchUsers = async (userCount) => {
    try {
        const {data: {results}} = await axios.get('https://randomuser.me/api/?results=' + userCount)

        return results.map(result => ({
            id: result.login.uuid,
            firstName: result.name.first,
            lastName: result.name.last,
            phone: result.phone,
            email: result.email,
            age: result.age,
            picture: result.picture.thumbnail
        }))
    } catch (e) {
        console.error(e);
        return []
    }

};

export const App = () => {

    const [users, setUsers] = useState([]);
    const [num, setNum] = useState(0);
    const [search, setSearch] = useState('');

    const handleFetchClick = () => {
        fetchUsers(10).then((newUsers) => setUsers([...users,...newUsers]));
    };

    const sortUsersOnClick = () => {
        setUsers(users => [...users].sort(function(a, b) {
                const textA = a.email.toUpperCase();
                const textB = b.email.toUpperCase();
                return (textA < textB) ? -1 : (textA > textB) ? 1 : 0;
            })
        )
    }

    const filteredUser = users.filter( user => {
        if(user.firstName.toLowerCase().includes(search.toLowerCase())){
            return user.firstName.toLowerCase().includes(search.toLowerCase())
        }
       else if (  user.phone.toLowerCase().includes(search.toLowerCase()))
        {
            return user.phone.toLowerCase().includes(search.toLowerCase())
        }
        else if (  user.email.toLowerCase().includes(search.toLowerCase()))
        {
            return user.email.toLowerCase().includes(search.toLowerCase())
        }
        else if (  user.lastName.toLowerCase().includes(search.toLowerCase()))
        {
            return user.lastName.toLowerCase().includes(search.toLowerCase())
        }
    })

    useEffect(async () => {
            fetchUsers(5).then(users => setUsers(users));
        },
        [num]);


    return(
        <div className="container-fluid">
            <div id="app">
                <Header onFetchClick={handleFetchClick}/>
                <input type="text" placeholder="Rechercher" onChange={ e => setSearch(e.target.value)}/>
                <ButtonSort buttonName="Sort" onClick={sortUsersOnClick}/>
                <Table users={filteredUser}/>
            </div>
        </div>
    )
};
